function getWordDatabase(nativeLang, targetLang) {
    if (languageData.wordPacks[nativeLang] && languageData.wordPacks[nativeLang][targetLang]) {
        return languageData.wordPacks[nativeLang][targetLang];
    }
    return [];
}

// ì§€ì› ì–¸ì–´ ëª©ë¡ ê°€ì ¸ì˜¤ê¸°
function getSupportedLanguages() {
    return languageData.languages;
}

// ì‚¬ìš© ê°€ëŠ¥í•œ í•™ìŠµ ì¡°í•© ê°€ì ¸ì˜¤ê¸°
function getAvailableCombinations() {
    const combinations = [];
    for (const native in languageData.wordPacks) {
        for (const target in languageData.wordPacks[native]) {
            combinations.push({
                native: native,
                target: target,
                nativeName: languageData.languages[native].name,
                targetName: languageData.languages[target].name,
                nativeFlag: languageData.languages[native].flag,
                targetFlag: languageData.languages[target].flag
            });
        }
    }
    return combinations;
}

